sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

      var rightNow = new GlideDateTime();
	var overDueNITTasks = new GlideRecord('x_58872_needit_needit_task');
	overDueNITTasks.addQuery('due_date','<=',rightNow);
	overDueNITTasks.addQuery('state','!=',3);
	overDueNITTasks.addQuery('assigned_to',current.assigned_to);
	overDueNITTasks.query();

template.print('Number of over due Needit Tasks assigned to you are :'+ (overDueNITTasks.getRowCount()-1) + '.');
})(current, template, email, email_action, event);
